-- TEXT字段UPDATE和DELETE测试
-- 测试溢出页的正确释放和重新分配

-- 1. 创建测试表
CREATE TABLE test_update (id INT, content TEXT);

-- 2. 插入测试数据
-- 短TEXT
INSERT INTO test_update VALUES (1, 'Short text A');

-- 长TEXT（需要溢出页，788字节）
INSERT INTO test_update VALUES (2, '');

-- 短TEXT（不需要溢出页）
INSERT INTO test_update VALUES (3, 'Medium text CCC');

-- 3. 初始查询
SELECT * FROM test_update;

-- 4. UPDATE测试

-- 4.1 短→短
UPDATE test_update SET content = 'Updated short' WHERE id = 1;

-- 4.2 短→长（分配溢出页）
UPDATE test_update SET content = '' WHERE id = 3;

-- 4.3 长→短（释放溢出页）
UPDATE test_update SET content = 'Now short' WHERE id = 2;

-- 5. 更新后查询
SELECT * FROM test_update;

-- 6. DELETE测试

-- 6.1 删除有溢出页的记录
DELETE FROM test_update WHERE id = 3;

-- 6.2 删除无溢出页的记录
DELETE FROM test_update WHERE id = 1;

-- 7. 删除后查询
SELECT * FROM test_update;

-- 8. 清理
DROP TABLE test_update;

exit

